    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

        if(conn.createUser(jTextField1.getText(), jTextField2.getText())){
            JOptionPane.showMessageDialog(this.nd, "OK", "Log off", JOptionPane.INFORMATION_MESSAGE);
        }
        else{
            JOptionPane.showMessageDialog(this.nd, "Cannot create this user", "Log off", JOptionPane.INFORMATION_MESSAGE);
        }
        this.dispose();
    }//GEN-LAST:event_jButton1ActionPerformed

